        public int determineSantaFloor(string input)
        {
            int floor = 0;
            List<char> chars = input.ToList<char>();
            foreach (char c in chars)
            {
                if (c == '(')
                {
                    ++floor;
                }
                if (c == ')')
                {
                    --floor;
                }
            }

            return floor;
        }

        public int determineFirstBasement(string input)
        {
            int counter = 0;
            int floor = 0;
            int output = 0;
            bool found = false;

            List<char> chars = input.ToList<char>();
            foreach (char c in chars)
            {
                ++counter;
                if (c == '(')
                {
                    ++floor;
                }
                if (c == ')')
                {
                    --floor;
                }
                if (floor < 0 && !found)
                {
                    output = counter;
                    found = true;
                }
            }

            return output;
        }
    }
